package org.openshapa.event;

import java.io.File;
import java.util.EventObject;

/**
 * Event object for file drag and drop.
 */
public final class FileDropEvent extends EventObject {

    /**
     * Auto-generated by Eclipse.
     */
    private static final long serialVersionUID = -2782985740353242459L;

    /**
     * The files involved in the drop event.
     */
    private final Iterable<File> files;

    /**
     * Creates a new FileDropEvent.
     *
     * @param source The source of the event.
     * @param droppedFiles The files involved in the drop event.
     */
    public FileDropEvent(final Object source,
            final Iterable<File> droppedFiles) {
        super(source);
        files = droppedFiles;
    }

    /**
     * @return The files involved in the drop event.
     */
    public Iterable<File> getFiles() {
        return files;
    }

}
